      def prefix
        %w(H. Dr. Drs. Ir. Bpk. Dra. Hj. DR. Rd. Ibu Ny.).rand
      end

      def suffix
        %w(S.E S.T S.Pd M.Pd S.H M.M).rand
      end

      def formats
      [
        Proc.new { [ first_name] },
        Proc.new { [ last_name] },
        Proc.new { [ prefix, first_name ] },
        Proc.new { [ prefix, first_name, suffix ] },
        Proc.new { [ prefix, first_name, last_name ] },
        Proc.new { [ first_name, last_name, suffix ] },
        Proc.new { [ first_name, last_name, suffix ] },
        Proc.new { [ prefix, first_name, last_name, suffix ] },
        Proc.new { [ prefix, first_name, last_name, suffix ] },
        Proc.new { [ first_name, first_name, last_name ] },
        Proc.new { [ first_name, last_name, last_name ] },
        Proc.new { [ first_name, last_name, last_name, suffix ] },
        Proc.new { [ prefix, first_name, last_name, last_name, suffix ] },
        Proc.new { [ prefix, prefix, first_name, last_name, last_name, suffix ] },
        Proc.new { [ prefix, first_name, first_name, last_name, suffix, suffix ] },
        Proc.new { [ first_name, last_name ] },
        Proc.new { [ first_name, last_name ] },
        Proc.new { [ first_name, last_name ] },
        Proc.new { [ first_name, last_name ] },
        Proc.new { [ first_name, last_name ] },
        Proc.new { [ first_name, last_name ] },
        Proc.new { [ first_name, last_name ] },
        Proc.new { [ first_name, last_name ] }
      ]
    end
